import React, { Component } from 'react'

export default class Form extends Component {
  constructor() {
    super();

    this.state = {
      name: '',
      email: '',
      age: '',
      cpf: '',
      endereco: '',
      cidade: '',
      cargo: '',
      descCargo: '',
    }

    this.handleChange = this.handleChange.bind(this)
  }

  // forma 1

  // handleChange(event) {
  //   this.setState({
  //     email:event.target.value,
  //   });
  // }

  // forma 2

  // handleChange({ target }) {
  //   const { name, value } = target;
  //   this.setState({
  //     [name]: value,
  //   });
  // }

  // forma 3 - tratando junto o checkbox

  handleChange({ target }) {
    const { name } = target;
    const value = (target.type === 'checkbox') ? target.checked : target.value;
    this.setState({
      [name]: value,
    });
  }

  render() {
    const { name, email, age, cpf, endereco, cidade, cargo, descCargo } = this.state

    return (
      <div>
        <h1>Estados e React - Tecnologia fantástica ou reagindo a regionalismos?</h1>
        <form className="form">
          <fieldset>
            <label htmlFor="name">
              <input
                type="text"
                id="name"
                name="name"
                placeholder="Name"
                maxLength="40"
                autofocus
                required
                onChange={ this.handleChange }
                value={ name }
            />
            </label>
            <label htmlFor="email">
              <input
                type="email"
                id="email"
                name="email"
                placeholder="E-mail"
                maxLength="50"
                required
                onChange={ this.handleChange }
                value={ email }
            />
            </label>
            <label htmlFor="age">
              Idade:
              <select
                id="age"
                name="age"
                onChange={ this.handleChange }
                value={ age }
              >
                <option value="">Selecione</option>
                <option value="adult">Maior que 18</option>
                <option value="underage">Menor que 18</option>
              </select>
              </label>
            <label htmlFor="cpf">
              <input
                type="text"
                id="cpf"
                name="cpf"
                placeholder="CPF"
                maxLength="11"
                required
                onChange={ this.handleChange }
                value={ cpf }
            />
            </label>
            <label htmlFor="endereco">
              <input
                type="text"
                id="endereco"
                name="endereco"
                placeholder="Endereço"
                maxLength="200"
                required
                onChange={ this.handleChange }
                value={ endereco }
            />
            </label>
            <label htmlFor="">
              <input
                type="text"
                id="cidade"
                name="cidade"
                placeholder="Cidade"
                maxLength="28"
                required
                onChange={ this.handleChange }
                value={ cidade }
            />
            </label>
          </fieldset>
          <fieldset>
          <label htmlFor="cargo">
              <textarea
                id="cargo"
                name="cargo"
                maxLength="500"
                required
                placeholder="Cargo"
                onChange={ this.handleChange }
                value={ cargo }
              />
            </label>
          <label htmlFor="descCargo">
              <textarea
                id="descCargo"
                name="descCargo"
                maxLength="500"
                required
                placeholder="Descrição do Cargo"
                onChange={ this.handleChange }
                value={ descCargo }
              />
            </label>
          </fieldset>
        </form>
      </div>
    )
  }
}
